import React from "react";
import "./styles.css";
import { useForm } from 'react-hook-form';


function EmailForm() {

    const { register, handleSubmit, watch, errors } = useForm();
    const onSubmit = data => console.log(data);

    return(
        <div className="contact">
            <h1>Contact</h1>
            <form id='contact-form' onSubmit={handleSubmit(onSubmit)}>
                <input type='text' name='user_name' placeholder='Name' {...register('user_name', {required: true })}/>
                {errors && errors.type === "required" && (
                    <div className="error">You must enter your name.</div>
                )}
                <br/>
                <input type='email' name='user_email' {...register('email', {required: true })} placeholder='Email' />
                <br/>
                <textarea name='message' {...register('message', {required: true })} placeholder='Message'/>
                <br/>
                <input type='submit' value='Send' />
            </form>
        </div>
    );
};

export default EmailForm;
