import React from "react";
import "./styles.css";
import { useForm } from 'react-hook-form';


function EmailForm() {

    const { handleSubmit, register, watch, formState: {errors} } = useForm();
    const onSubmit = values => console.log(values);

    const message = watch('message') || "";
    const messageCharactersWatch = 250 - message.length;

    return(
        <div className="contact">
            <h1>Contact</h1>
            <form id='contact-form' onSubmit={handleSubmit(onSubmit)}>

                {errors.username && errors.username.type === "required" && (
                    <div role="alert">Name is required<br/></div>
                )}
                <input 
                    placeholder="Name"
                    aria-invalid={errors.username ? "true" : "false"}
                    {...register('username', {
                        required: true
                        })}
                    />
                <br/>

                {errors.email && errors.email.type === "required" && (
                    <div role="alert">Email is required<br/></div>
                )}
                <input 
                type='email'
                placeholder='Email'
                {...register('email', {
                    required: true,
                    pattern: {
                        value: /^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,}$/i,
                        message: "Invalid email address."
                        } 
                    })}  />
                <br/>

                {errors.message && errors.message.type === "required" && (
                    <div role="alert">A message is required<br/></div>
                )}
                <textarea 
                name='message'
                placeholder='Message'
                maxLength='250'
                {...register('message', {
                    required: true 
                })} 
                />
                <p className="message-chars">{messageCharactersWatch}</p>
                <br/>
                <button type='submit'>Submit</button>
            </form>
        </div>
    );
};

export default EmailForm;
