import React from 'react';
import "./styles.css";

function Navigation() {

// When the user scrolls down 20px from the top of the document, slide down the navbar
// When the user scrolls to the top of the page, slide up the navbar (50px out of the top view)
window.onscroll = function() {scrollFunction()};

function scrollFunction() {
  if (document.body.scrollTop > 850 || document.documentElement.scrollTop > 850) {
    document.getElementById("navbar").style.top = "0";
  } else {
    document.getElementById("navbar").style.top = "-50px";
  }
}

window.addEventListener("scroll", function() {
    let elementTarget = this.document.getElementById("header");

    if (this.window.scrollY > (elementTarget.offsetTop + elementTarget.offsetHeight)) {
        this.alert("you scrolled past the first element!")
    }
})

    return (
        <div>
            <section>
                        <div id="navbar">
                            <a id="logo">SQ-DPT Logo</a>
                            <div id="navbar-right">
                                <a className="active">SQ-DPT</a>
                                <a>About</a>
                                <a>Contact</a>
                            </div>
                        </div>
            </section>
        </div>
    );
}

export default Navigation;
