import React from "react";
import { useState } from "react";
import './styles.css'

export default function Contact() {

	const [ mailerState, setMailerState ] = useState({
		name: "",
		email: "",
		subject: "",
		message: "",
	})

	function handleStateChange(e) {
		setMailerState((prevState) => ({
			...prevState,
			[e.target.name]: e.target.value,
		}));
	}

	const submitEmail = async (e) => {
		e.preventDefault();
		console.log({ mailerState });
		const response = await fetch("/send", {
			method: "PUT",
			headers: {
				"Content-type": "application/json",
			},
			body: JSON.stringify({ mailerState }),
		})
			.then((res) => res.json())
			.then(async (res) => {
				const resData = await res;
				console.log(resData);
				if (resData.status === "success") {
					alert("Message sent");
				} else if (resData.status === "fail") {
					alert("Message failed to send.")
				}
			})
			.then(() => {
				setMailerState({
					email: "",
					name: "",
					subject: "",
					message: "",
				});
			});
	};

  const regex =
    /(?:[a-z0-9!#$%&'*+/=?^_`{|}~-]+(?:\.[a-z0-9!#$%&'*+/=?^_`{|}~-]+)*|"(?:|\\)*")@(?:(?:[a-z0-9](?:[a-z0-9-]*[a-z0-9])?\.)+[a-z0-9](?:[a-z0-9-]*[a-z0-9])?|\[(?:(?:(2(5[0-5]|[0-4][0-9])|1[0-9][0-9]|[1-9]?[0-9]))\.){3}(?:(2(5[0-5]|[0-4][0-9])|1[0-9][0-9]|[1-9]?[0-9])|[a-z0-9-]*[a-z0-9]:)\])/;

  const messageCharactersWatch = 500 - mailerState.message.length;

  return (
		<div id='contact' className='contactContainer'>
		<p className='contactTitle'>Reach out</p>
		<p className='contactText'>Need help with a project? have a cool opportunity? Or maybe you just want to say hey!  Please shoot me an email and I will get back to you as soon as I can.</p>
		<button className='contactBtn' type="button" onClick={() => router.push('mailto:chriswolfe.dev@gmail.com')}>
		Email me
	</button>
	</div>
  );
}
