import React, { useEffect, useState } from "react";
import "./carousel.css";

export const CarouselItem = ( { children, width } ) => {
	return (
		<div className="carousel-item" style={ { width: width } }>
			{children}
		</div>
	);
};

const Carousel = ( { children } ) => {
	const [activeIndex, setActiveIndex] = useState(0)
	const [paused, setPaused] = useState(false);
	const [touchpoint, setTouchPoint] = useState(null);

	const handleTouchPoint = (event) => {
		const userTouch = event.touches[0].clientX
		setTouchPoint(userTouch)
	}

	const handleTouchMove = (event) => {
		const touchPoint = touchPoint

		if (touchPoint === null) {
			return
		}

		const currentTouch = event.touches[0].clientX
		const diff = touchPoint - currentTouch

		if (diff > 5) {
			updateIndex(activeIndex + 1)
		}

		if (diff < -5) {
			updateIndex(activeIndex - 1)
		}

		setTouchPoint(null)
	}

	const updateIndex = (newIndex) => {
		if (newIndex < 0) {
			newIndex = React.Children.count(children) - 1;
		} else if (newIndex >= React.Children.count(children)) {
			newIndex = 0;
		}

		setActiveIndex(newIndex)
	}

	useEffect(() => {
		const interval = setInterval(() => {
			if (!paused) {
				updateIndex(activeIndex + 1);
			}
		}, 10000);

		return () => {
			if (interval) {
				clearInterval(interval);
			}
		};
	});

	return (
		<div className="carousel"
			onMouseEnter={() => setPaused(true)}
			onMouseLeave={() => setPaused(false)}
			onTouchStart={handleTouchMove}
			>
			<div className="inner" style={ { transform: `translateX(-${activeIndex * 100}%)` } }>
				{React.Children.map(children, (child, index) => {
					return React.cloneElement(child, { width: "100%" });
				})};
			</div>
			<div className="indicators">
				<button
					onClick={() => {
						updateIndex(activeIndex - 1)
					}}
					>
						Prev
					</button>
					{React.Children.map(children, (child, index) => {
						return (
							<button className={`${index === activeIndex ? "active" : ""}`} onClick={() => {
								updateIndex(index);
							}}
							>
								{index + 1}
							</button>
						)
					})}
					<button onClick={() => {
						updateIndex(activeIndex + 1);
					}}
					>
						Next
					</button>
			</div>
		</div>
	)
}

export default Carousel;
